mapper.select:
		select id,order_no,user_id,commodity_code,count,amount from t_order
		where 1=1
            AND order_no = #{obj.search.orderNo}
            AND user_id = #{obj.search.userId}
            AND commodity_code = #{obj.search.commodityCode}
            AND count = #{obj.search.count}
            AND amount = #{obj.search.amount}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,order_no,user_id,commodity_code,count,amount from t_order
		where 1=1
            AND order_no = #{obj.orderNo}
            AND user_id = #{obj.userId}
            AND commodity_code = #{obj.commodityCode}
            AND count = #{obj.count}
            AND amount = #{obj.amount}

         AND platform_id = #{platformId}

mapper.update:
update t_order
order_no=#{obj.orderNo}, user_id=#{obj.userId}, commodity_code=#{obj.commodityCode}, count=#{obj.count}, amount=#{obj.amount},
where id=#{obj.id} AND platform_id = #{platformId}
