mapper.select:
		select id,user_id,amount from t_account
		where 1=1
            AND user_id = #{obj.search.userId}
            AND amount = #{obj.search.amount}

         AND platform_id = #{platformId}
         order by ${obj.orderField} ${obj.orderType}

		select id,user_id,amount from t_account
		where 1=1
            AND user_id = #{obj.userId}
            AND amount = #{obj.amount}

         AND platform_id = #{platformId}

mapper.update:
    	update t_account 

           user_id=#{obj.userId},
           amount=#{obj.amount},


		where id=#{obj.id} 
         AND platform_id = #{platformId}
